import { useSelector, useDispatch } from "react-redux";
import { toast } from "react-hot-toast";
import { add, remove } from '../redux/Slices/CartSlice';

const Product = ({ post }) => {
  const { cart } = useSelector((state) => state);
  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(add(post));
    toast.success("Item added to cart");
  };

  const removeFromCart = () => {
    dispatch(remove(post.id));
    toast.success("Item removed from cart");
  };

  return (
    <div className="flex flex-col items-center justify-between shadow-lg hover:scale-110 transition duration-300 ease-in  ">
      <div>
        <p>{post.title}</p>
      </div>
      <div>
        <p>{post.description .split(" ").slice(0,10).join(" ")+"..."}</p>
      </div>
      <div>
        <img src={post.image} alt={post.title} />
      </div>
      <div>
        <p>{post.price}</p>
      </div>
      <button onClick={cart.some((p) => p.id === post.id) ? removeFromCart : addToCart}>
        {cart.some((p) => p.id === post.id) ? "Remove Item" : "Add to Cart"}
      </button>
    </div>
  );
};

export default Product;
